import React, { useState } from 'react';

function Capitulo(props) {
    const [visivel, setVisivel] = useState(true);

    return (
        <div>
            <h2>{props.numero ? `Capítulo ${props.numero} - ${props.titulo}` : props.titulo}</h2>
            {visivel && <h1>{props.descricao}</h1>}
            <button onClick={() => setVisivel(!visivel)}>{visivel ? "Esconder" : "Mostrar"}</button>
        </div>
    );
}

const livros = [
    {
        titulo: "NOME DO LIVRO",
        capitulos: []
    }
];

export default function Livro() {
    const capitulos = [
        { numero: "1", nome: "Introdução", descricao: "Descrição da Introdução" },
        { numero: "2", nome: "Desenvolvimento", descricao: "Descrição do Desenvolvimento" },
        { numero: "3", nome: "Conclusão", descricao: "Descrição da Conclusão" },
        { numero: null, nome: "Capítulo Especial", descricao: "Descrição do Capítulo Especial" },
        { numero: "5", nome: "Epílogo", descricao: "Descrição do Epílogo" },
    ];

    return (
        <>
            <h1>Livro INTERESSANTE</h1>
            {capitulos.map((capitulo, index) => {
                return <Capitulo key={"capitulo-" + index} numero={capitulo.numero} titulo={capitulo.nome} descricao={capitulo.descricao} />;
            })}
        </>
    );
}
