import React, { useState } from 'react';

const livros = [
    {
        titulo: "Livro 1",
        capitulos: [
            { numero: "1", nome: "Introdução", descricao: "Descrição da Introdução" },
            { numero: "2", nome: "Desenvolvimento", descricao: "Descrição do Desenvolvimento" },
            { numero: "3", nome: "Conclusão", descricao: "Descrição da Conclusão" },
            { numero: null, nome: "Capítulo Especial", descricao: "Descrição do Capítulo Especial" },
            { numero: "5", nome: "Epílogo", descricao: "Descrição do Epílogo" },
        ]
    },
    {
        titulo: "Livro 2",
        capitulos: [
            { numero: "1", nome: "Introdução", descricao: "Descrição da Introdução" },
            { numero: "2", nome: "Desenvolvimento", descricao: "Descrição do Desenvolvimento" },
            { numero: "3", nome: "Conclusão", descricao: "Descrição da Conclusão" },
        ]
    },
    {
        titulo: "Livro 3",
        capitulos: [
            { numero: "1", nome: "Introdução", descricao: "Descrição da Introdução" },
            { numero: "2", nome: "Desenvolvimento", descricao: "Descrição do Desenvolvimento" },
        ]
    }
];

function Livro(props) {
    const [visivel, setVisivel] = useState(true);

    return (
        <div>
            <h1 onClick={() => setVisivel(!visivel)}>{props.titulo}</h1>
            {visivel && (
                <div>
                    {props.capitulos.map((capitulo, index) => (
                        <div key={index}>
                            <h2>{capitulo.numero ? `Capítulo ${capitulo.numero} - ${capitulo.nome}` : capitulo.nome}</h2>
                            <h1>{capitulo.descricao}</h1>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default function App() {
    return (
        <>
            {livros.map((livro, index) => (
                <Livro key={index} titulo={livro.titulo} capitulos={livro.capitulos} />
            ))}
        </>
    );
}
