import React from 'react';

function Capitulo(props) {
    // Verifica se o número do capítulo é null e renderiza o título sem o prefixo "Capítulo #"
    return <h2>{props.numero ? `Capítulo ${props.numero} - ${props.titulo}` : props.titulo}</h2>;
}

const livros = [
    {
        titulo: "NOME DO LIVRO",
        capitulos: []
    }
];

export default function Livro() {
    const capitulos = [
        { numero: "1", nome: "Introdução", descricao: "" },
        { numero: "2", nome: "Desenvolvimento" },
        { numero: "3", nome: "Conclusão" },
        { numero: "null", nome: "Capítulo Especial" }, // Adiciona um capítulo com número null
        { numero: "5", nome: "Epílogo" }, // Adiciona mais um capítulo para totalizar 5
    ];

    return (
        <>
            <h1>Livro INTERESSANTE</h1>
            {capitulos.map((capitulo, index, descricao) => {
                return <Capitulo key={"capitulo-" + index} numero={capitulo.numero} titulo={capitulo.nome}>
                    <h1>{descricao}</h1>
                    </Capitulo>;
            })}
        </>
    );
}
